import java.util.Scanner;


public class Test1206 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner scan=new Scanner(System.in);
		while(scan.hasNext()){
			String a=scan.next();
			String b=scan.next();
			System.out.println(a+b);
		}
		scan.close();
	}
}
